mapper.select:
SELECT id, CREATOR, project_name, project_type, project_status, project_url, project_dueDate, createdAt, updatedAt
FROM ontrack_project
WHERE id = #{id}

SELECT id as memberId, user_id, project_id, nickname
FROM project_member
WHERE project_id = #{projectId}

    AND user_id = #{userId}


    And member_id = #{memberId}

SELECT creator, project_type, project_name, project_dueDate, project_status, createdAt
FROM ontrack_project
WHERE project_id = #{projectId}
