mapper.select:
SELECT project_id, task_title, authorMid, authorName, task_priority, task_status, task_dueDate, createdAt, updatedAt, updatedBy
FROM ontrack_task
WHERE id = #{id}

SELECT task_id, nickname, member_id
FROM task_assignment
WHERE task_id = #{taskId}

SELECT id, task_id, author_mid, author_name, type, comment, createdAt, modifiedAt, blockedBy
FROM task_comment
WHERE task_id = #{taskId}
ORDER BY createdAt DESC;

SELECT tf.id, tf.file_origName, tf.file_newName, tf.file_type, tf.file_size, pm.id as memberId, pm.nickname as uploaderName, tf.createdAt, tf.deletedBy
FROM task_file tf
INNER JOIN project_member pm
ON tf.member_id = pm.id
WHERE tf.task_id = #{taskId}
ORDER BY tf.createdAt DESC;

SELECT count(task_id)
FROM task_assignment
WHERE task_id = #{taskId}
GROUP BY task_id;

SELECT task_id
FROM task_assignment
WHERE task_id = #{taskId}
AND member_id = #{memberId}

mapper.update:
UPDATE ontrack_task
SET task_title = #{title}
, updatedAt = #{updatedAt}
, updatedBy = #{updatedBy}
WHERE id = #{taskId}

UPDATE ontrack_task
SET task_status = #{status}
, updatedAt = #{updatedAt}
, updatedBy = #{updatedBy}
WHERE id = #{taskId}

UPDATE ontrack_task
SET task_dueDate = #{dueDate}
, updatedAt = #{updatedAt}
, updatedBy = #{updatedBy}
WHERE id = #{taskId}

UPDATE task_comment
SET comment = #{comment}
, modifiedAt = #{modifiedAt}
WHERE id = #{id}

UPDATE task_comment
SET blockedBy = #{blockedBy}
, blockedAt = #{blockedAt}
WHERE id = #{commentId}

UPDATE task_file
SET file_newName = ""
, file_path = ""
, deletedBy = #{deletedBy}
, deletedAt = #{deletedAt}
WHERE id = #{id}

mapper.delete:
DELETE
FROM task_assignment
WHERE task_id = #{taskId}
AND member_id = #{memberId}
