mapper.select:
SELECT task_title, AUTHOR, task_status, createdAt
FROM ontrack_task
WHERE id = #{id}

SELECT task_id, nickname, member_id
FROM task_assignment
WHERE task_id = #{taskId}

mapper.update:
UPDATE ontrack_task
SET task_status = #{status}
, updatedAt = #{updatedAt}
, updatedBy = #{updatedBy}
WHERE id = #{taskId}
